use warnings; use strict;my %words=(); my @counts=(); print "\nft2.txt\n"; while(<>){my %uniq=(); foreach (split){if(length){$words{$_}++;$uniq{$_}++}} push @counts, scalar keys %uniq; my $size = @counts; @counts = sort @counts; printf"%.1f\n", ($size%2==0) ? ($counts[$size/2-1]+$counts[$size/2])/2 : $counts[$size/2]} print "\nft1.txt:\n"; foreach my $word (sort keys(%words)){printf"%-25s\t%d\n", $word, $words{$word};}
